import React, {useState, useEffect} from "react";
import { StatusBar } from 'expo-status-bar';
import { Button, Image, SafeAreaView, ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
import axios from 'axios'

export default function HomeScreen() {
  const [data, setData] = useState([]);
  const [text, setText] =useState([])

  const fetch = () => {
    
    axios.get('http://127.0.0.1:8000/emp/list-employee')
    .then((res) => setData(res.data))
    .catch(err => console.log(err))
  }

  const dele = (id) => {
    axios.delete(`http://127.0.0.1:8000/emp/empdel/${id}`).then(
      fetch()
    )
  }
  const upd = (id) => {
    axios.post('http://127.0.0.1:8000/emp/list-employee')
    .then((res) => setText(res.text))
    .catch(err => console.log(err))
  }
  useEffect(() => {
    fetch()
    upd()
  },[])


    return (
      <View  style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        {data.map((res) => {
          return (
            <View>
            <Text>{res.name}</Text>
            <Button
              title="Press me"
              onPress={() => dele(res.id)}
            />
             <TextInput
                style={{height: 40}}
                onChangeText={res => setText(res.id)}
                value={res.name}
              />
              <Button
              title="Update"
              onPress={() => upd(res.id)}
              />
          </View>
          )
        })}
      </View>
    );
  }
